import { useState } from 'react';
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Clock, MapPin } from "lucide-react";
import { useLanguage } from "@/hooks/useLanguage";
import { useAuth } from "@/hooks/use-auth";
import ActivityRating from "./activity-rating";
import ActivityPreview from "./activity-preview";
import { getAssetUrl, getActivityFallbackImage } from '@/lib/image-utils';
import type { ActivityType } from "@shared/schema";

interface ActivityCardProps {
  activity: ActivityType;
  showDescription?: boolean;
}

export default function ActivityCard({ activity, showDescription = false }: ActivityCardProps) {
  const { t } = useLanguage();
  const { user } = useAuth();
  const [showPreview, setShowPreview] = useState(false);

  // Only show admin features to authenticated admins
  const isAdmin = user && (user.role === 'admin' || user.role === 'superadmin');



  return (
    <Card className="overflow-hidden shadow-lg hover:shadow-xl transition-all duration-300 group bg-white">
      <div className="relative overflow-hidden">
        <img
          key={activity._id + Date.now()}
          src={activity.image + '?v=' + Date.now() + '&bust=' + Math.random()}
          alt={activity.name}
          className="w-full h-64 object-cover group-hover:scale-110 transition-transform duration-500"
          style={{ objectPosition: 'center' }}
          onError={(e) => {
            // Force complete reload
            setTimeout(() => {
              e.currentTarget.src = activity.image + '?reload=' + Math.random();
            }, 100);
          }}
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent" />
        <div className="absolute top-4 right-4">
          <Badge variant="secondary" className="bg-moroccan-gold text-white">
            {activity.category}
          </Badge>
        </div>
        <div className="absolute bottom-4 left-4 text-white">
          <div className="text-3xl font-black text-white drop-shadow-2xl" style={{ textShadow: '2px 2px 4px rgba(0,0,0,0.8), 0 0 8px rgba(0,0,0,0.6)' }}>
            {activity.price} MAD
          </div>
          {isAdmin && (
            <div className="text-xs bg-green-600 bg-opacity-90 px-2 py-1 rounded-full mt-1">
              Save {((activity.getyourguidePrice || Number(activity.price) + 150) - Number(activity.price))} MAD vs GetYourGuide
            </div>
          )}
        </div>
      </div>

      <CardContent className="p-6">
        <h3 className="font-playfair text-xl font-bold text-moroccan-blue mb-2">
          {activity.name}
        </h3>

        {showDescription && (
          <p className="text-gray-600 mb-4 line-clamp-3">
            {activity.description}
          </p>
        )}

        <div className="flex items-center justify-between text-sm text-gray-500 mb-4">
          <div className="flex items-center">
            <Clock className="h-4 w-4 mr-1" />
            <span>{activity.duration || 'Full day'}</span>
          </div>
          <div className="flex items-center">
            <MapPin className="h-4 w-4 mr-1" />
            <span>Marrakech</span>
          </div>
        </div>

        {/* Price Comparison Summary - Admin Only */}
        {isAdmin && (
          <div className="bg-blue-50 p-3 rounded-lg mb-4 border border-blue-200">
            <div className="text-sm font-medium text-moroccan-blue mb-2">Price Comparison</div>
            <div className="grid grid-cols-2 gap-3 text-xs">
              <div>
                <div className="text-green-700 font-medium">Our Price</div>
                <div className="text-lg font-bold text-green-600">{activity.price} MAD</div>
              </div>
              <div>
                <div className="text-orange-700 font-medium">GetYourGuide</div>
                <div className="text-lg font-bold text-orange-600">{activity.getyourguidePrice || Number(activity.price) + 150} MAD</div>
              </div>
            </div>
            <div className="text-center mt-2 text-xs text-green-600 font-medium">
              You Save: {((activity.getyourguidePrice || Number(activity.price) + 150) - Number(activity.price))} MAD per person
            </div>
          </div>
        )}

        <Button 
          className="w-full bg-moroccan-red hover:bg-red-600 text-white transition-all duration-300 transform hover:scale-105"
          onClick={() => setShowPreview(true)}
        >
          View Details & Book
        </Button>

        <ActivityPreview
          activity={activity}
          isOpen={showPreview}
          onClose={() => setShowPreview(false)}
          onBookNow={() => {
            // Navigate to booking page with activity ID as URL parameter
            window.location.href = `/booking?activity=${activity.id || activity._id}`;
          }}
        />
      </CardContent>
    </Card>
  );
}

client/src/components/activity-rating.ts.content:
import { useQuery } from "@tanstack/react-query";
import { Star } from "lucide-react";
import { Badge } from "@/components/ui/badge";

interface ActivityRatingProps {
  activityId: string;
  className?: string;
  showReviewCount?: boolean;
}

interface RatingData {
  averageRating: number;
  totalReviews: number;
}

export default function ActivityRating({ 
  activityId, 
  className = "", 
  showReviewCount = true 
}: ActivityRatingProps) {
  const { data: rating, isLoading } = useQuery<RatingData>({
    queryKey: [`/api/activities/${activityId}/rating`],
    queryFn: async () => {
      const response = await fetch(`/api/activities/${activityId}/rating`);
      if (!response.ok) throw new Error("Failed to fetch rating");
      return response.json();
    },
  });

  if (isLoading) {
    return (
      <div className={`flex items-center space-x-1 ${className}`}>
        <div className="w-4 h-4 bg-gray-200 rounded animate-pulse"></div>
        <div className="w-8 h-4 bg-gray-200 rounded animate-pulse"></div>
      </div>
    );
  }

  if (!rating || rating.totalReviews === 0) {
    return (
      <div className={`flex items-center space-x-1 text-gray-500 ${className}`}>
        <Star className="w-4 h-4" />
        <span className="text-sm">No reviews yet</span>
      </div>
    );
  }

  const renderStars = (rating: number) => {
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 >= 0.5;
    const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);

    return (
      <div className="flex items-center">
        {/* Full stars */}
        {Array(fullStars).fill(null).map((_, i) => (
          <Star key={`full-${i}`} className="w-4 h-4 fill-yellow-400 text-yellow-400" />
        ))}

        {/* Half star */}
        {hasHalfStar && (
          <div className="relative">
            <Star className="w-4 h-4 text-gray-300" />
            <div className="absolute inset-0 overflow-hidden w-1/2">
              <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
            </div>
          </div>
        )}

        {/* Empty stars */}
        {Array(emptyStars).fill(null).map((_, i) => (
          <Star key={`empty-${i}`} className="w-4 h-4 text-gray-300" />
        ))}
      </div>
    );
  };

  return (
    <div className={`flex items-center space-x-2 ${className}`}>
      {renderStars(rating.averageRating)}
      <span className="text-sm font-medium text-gray-700">
        {rating.averageRating.toFixed(1)}
      </span>
      {showReviewCount && (
        <Badge variant="secondary" className="text-xs">
          {rating.totalReviews} {rating.totalReviews === 1 ? 'review' : 'reviews'}
        </Badge>
      )}
    </div>
  );
}

client/src/components/admin-route.tsx.content:
import { useEffect } from "react";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";

interface AdminRouteProps {
  children: React.ReactNode;
  requireSuperAdmin?: boolean;
}

export default function AdminRoute({ children, requireSuperAdmin = false }: AdminRouteProps) {
  const { toast } = useToast();
  const { user, isLoading, isAuthenticated } = useAuth();
  const [, setLocation] = useLocation();

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Authentication Required",
        description: "Please log in to access the admin area",
        variant: "destructive",
      });
      setTimeout(() => {
        setLocation("/admin/login");
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast, setLocation]);

  // Check for superadmin requirement
  useEffect(() => {
    if (!isLoading && isAuthenticated && requireSuperAdmin && user?.role !== 'superadmin') {
      toast({
        title: "Access Denied",
        description: "This area requires superadmin privileges",
        variant: "destructive",
      });
      setTimeout(() => {
        setLocation("/admin");
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, user, requireSuperAdmin, toast, setLocation]);

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-moroccan-blue mx-auto mb-4"></div>
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return null;
  }

  if (requireSuperAdmin && user?.role !== 'superadmin') {
    return null;
  }

  return <>{children}</>;
}

client/src/components/availability-calen.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Calendar } from "@/components/ui/calendar";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Separator } from "@/components/ui/separator";
import { 
  Calendar as CalendarIcon, 
  Clock, 
  Users, 
  DollarSign,
  Sunrise,
  Sun,
  Sunset,
  Moon,
  Check,
  X
} from "lucide-react";
import { format, addDays, isSameDay, isAfter, isBefore, startOfDay } from "date-fns";
import { useLanguage } from "@/hooks/useLanguage";

interface TimeSlot {
  id: string;
  time: string;
  label: string;
  capacity: number;
  booked: number;
  price: number;
  icon: React.ReactNode;
  available: boolean;
}

interface DayAvailability {
  date: Date;
  available: boolean;
  timeSlots: TimeSlot[];
  weather?: {
    condition: string;
    temperature: number;
    suitable: boolean;
  };
}

interface AvailabilityCalendarProps {
  activityId: string;
  activityName: string;
  basePrice: number;
  onDateTimeSelect: (date: Date, timeSlot: TimeSlot) => void;
  selectedDate?: Date;
  selectedTimeSlot?: TimeSlot;
}

export default function AvailabilityCalendar({
  activityId,
  activityName,
  basePrice,
  onDateTimeSelect,
  selectedDate,
  selectedTimeSlot
}: AvailabilityCalendarProps) {
  const { t } = useLanguage();
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedDay, setSelectedDay] = useState<Date | undefined>(selectedDate);
  const [viewMode, setViewMode] = useState<'calendar' | 'list'>('calendar');

  // Generate mock availability data (in real app, this would come from API)
  const generateAvailability = (date: Date): DayAvailability => {
    const today = startOfDay(new Date());
    const isToday = isSameDay(date, today);
    const isPast = isBefore(date, today);
    const isFarFuture = isAfter(date, addDays(today, 90));

    // Generate time slots based on activity type
    let timeSlots: TimeSlot[] = [];

    if (activityName.toLowerCase().includes('balloon')) {
      // Hot air balloon - early morning only
      timeSlots = [
        {
          id: 'sunrise',
          time: '05:30',
          label: 'Sunrise Flight',
          capacity: 16,
          booked: Math.floor(Math.random() * 12),
          price: basePrice + (isToday ? 50 : 0),
          icon: <Sunrise className="h-4 w-4" />,
          available: !isPast && !isFarFuture
        }
      ];
    } else if (activityName.toLowerCase().includes('desert')) {
      // Desert tours - multiple slots
      timeSlots = [
        {
          id: 'morning',
          time: '08:00',
          label: 'Morning Departure',
          capacity: 20,
          booked: Math.floor(Math.random() * 15),
          price: basePrice,
          icon: <Sun className="h-4 w-4" />,
          available: !isPast && !isFarFuture
        },
        {
          id: 'afternoon',
          time: '14:00',
          label: 'Afternoon Adventure',
          capacity: 20,
          booked: Math.floor(Math.random() * 15),
          price: basePrice + 25,
          icon: <Sunset className="h-4 w-4" />,
          available: !isPast && !isFarFuture
        },
        {
          id: 'sunset',
          time: '17:30',
          label: 'Sunset Experience',
          capacity: 24,
          booked: Math.floor(Math.random() * 18),
          price: basePrice + 50,
          icon: <Moon className="h-4 w-4" />,
          available: !isPast && !isFarFuture
        }
      ];
    } else {
      // Other activities - standard slots
      timeSlots = [
        {
          id: 'morning',
          time: '09:00',
          label: 'Morning Tour',
          capacity: 15,
          booked: Math.floor(Math.random() * 10),
          price: basePrice,
          icon: <Sun className="h-4 w-4" />,
          available: !isPast && !isFarFuture
        },
        {
          id: 'afternoon',
          time: '15:00',
          label: 'Afternoon Tour',
          capacity: 15,
          booked: Math.floor(Math.random() * 10),
          price: basePrice + 20,
          icon: <Sunset className="h-4 w-4" />,
          available: !isPast && !isFarFuture
        }
      ];
    }

    // Update availability based on capacity
    timeSlots = timeSlots.map(slot => ({
      ...slot,
      available: slot.available && slot.booked < slot.capacity
    }));

    return {
      date,
      available: timeSlots.some(slot => slot.available),
      timeSlots,
      weather: {
        condition: ['sunny', 'partly-cloudy', 'clear'][Math.floor(Math.random() * 3)],
        temperature: 20 + Math.floor(Math.random() * 15),
        suitable: Math.random() > 0.1 // 90% suitable weather
      }
    };
  };

  const { data: availability } = useQuery({
    queryKey: [`/api/activities/${activityId}/availability`, currentMonth],
    queryFn: () => {
      // Generate availability for the current month
      const days: DayAvailability[] = [];
      const startDate = new Date(currentMonth.getFullYear(), currentMonth.getMonth(), 1);
      const endDate = new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 0);

      for (let date = new Date(startDate); date <= endDate; date.setDate(date.getDate() + 1)) {
        days.push(generateAvailability(new Date(date)));
      }

      return days;
    },
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  const getDayAvailability = (date: Date) => {
    return availability?.find(day => isSameDay(day.date, date));
  };

  const getAvailableSpots = (timeSlot: TimeSlot) => {
    return timeSlot.capacity - timeSlot.booked;
  };

  const getAvailabilityStatus = (timeSlot: TimeSlot) => {
    const available = getAvailableSpots(timeSlot);
    if (available === 0) return { label: 'Sold Out', color: 'bg-red-100 text-red-800' };
    if (available <= 3) return { label: `${available} left`, color: 'bg-orange-100 text-orange-800' };
    return { label: 'Available', color: 'bg-green-100 text-green-800' };
  };

  const selectedDayData = selectedDay ? getDayAvailability(selectedDay) : null;

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <CalendarIcon className="h-5 w-5 text-moroccan-blue" />
          <h3 className="text-lg font-semibold text-moroccan-blue">
            Select Date & Time
          </h3>
        </div>
        <Select value={viewMode} onValueChange={(value: 'calendar' | 'list') => setViewMode(value)}>
          <SelectTrigger className="w-32">
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="calendar">Calendar</SelectItem>
            <SelectItem value="list">List View</SelectItem>
          </SelectContent>
        </Select>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Calendar View */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <CalendarIcon className="h-5 w-5" />
              <span>Available Dates</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <Calendar
              mode="single"
              selected={selectedDay}
              onSelect={setSelectedDay}
              onMonthChange={setCurrentMonth}
              disabled={(date) => {
                const dayData = getDayAvailability(date);
                return !dayData?.available || isBefore(date, startOfDay(new Date()));
              }}
              modifiers={{
                available: (date) => {
                  const dayData = getDayAvailability(date);
                  return dayData?.available || false;
                },
                booked: (date) => {
                  const dayData = getDayAvailability(date);
                  return dayData?.timeSlots.some(slot => slot.booked > slot.capacity * 0.8) || false;
                }
              }}
              modifiersStyles={{
                available: { 
                  backgroundColor: 'rgb(34, 197, 94)',
                  color: 'white',
                  fontWeight: 'bold'
                },
                booked: {
                  backgroundColor: 'rgb(249, 115, 22)',
                  color: 'white'
                }
              }}
              className="rounded-md border"
            />
            <div className="mt-4 flex items-center space-x-4 text-sm">
              <div className="flex items-center space-x-1">
                <div className="w-3 h-3 bg-green-500 rounded"></div>
                <span>Available</span>
              </div>
              <div className="flex items-center space-x-1">
                <div className="w-3 h-3 bg-orange-500 rounded"></div>
                <span>Limited</span>
              </div>
              <div className="flex items-center space-x-1">
                <div className="w-3 h-3 bg-gray-300 rounded"></div>
                <span>Unavailable</span>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Time Slots */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Clock className="h-5 w-5" />
              <span>
                {selectedDay ? format(selectedDay, 'EEEE, MMMM d') : 'Select a Date'}
              </span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            {selectedDayData ? (
              <div className="space-y-4">
                {selectedDayData.weather && (
                  <div className="p-3 bg-blue-50 rounded-lg">
                    <div className="flex items-center justify-between text-sm">
                      <span className="font-medium">Weather Forecast</span>
                      <span className={`px-2 py-1 rounded text-xs ${
                        selectedDayData.weather.suitable ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                      }`}>
                        {selectedDayData.weather.suitable ? 'Perfect Conditions' : 'Check Weather'}
                      </span>
                    </div>
                    <div className="mt-1 text-sm text-gray-600">
                      {selectedDayData.weather.condition.charAt(0).toUpperCase() + selectedDayData.weather.condition.slice(1)}, {selectedDayData.weather.temperature}Â°C
                    </div>
                  </div>
                )}

                <div className="space-y-3">
                  {selectedDayData.timeSlots.map((slot) => {
                    const status = getAvailabilityStatus(slot);
                    const isSelected = selectedTimeSlot?.id === slot.id;

                    return (
                      <div
                        key={slot.id}
                        className={`p-4 border-2 rounded-lg cursor-pointer transition-all ${
                          isSelected 
                            ? 'border-moroccan-blue bg-blue-50' 
                            : slot.available 
                              ? 'border-gray-200 hover:border-moroccan-blue hover:bg-gray-50' 
                              : 'border-gray-100 bg-gray-50 cursor-not-allowed opacity-60'
                        }`}
                        onClick={() => {
                          if (slot.available && selectedDay) {
                            onDateTimeSelect(selectedDay, slot);
                          }
                        }}
                      >
                        <div className="flex items-center justify-between">
                          <div className="flex items-center space-x-3">
                            <div className="text-moroccan-blue">
                              {slot.icon}
                            </div>
                            <div>
                              <div className="font-semibold text-gray-900">
                                {slot.time} - {slot.label}
                              </div>
                              <div className="text-sm text-gray-600 flex items-center space-x-2">
                                <Users className="h-3 w-3" />
                                <span>{getAvailableSpots(slot)} of {slot.capacity} spots available</span>
                              </div>
                            </div>
                          </div>

                          <div className="flex items-center space-x-3">
                            <div className="text-right">
                              <div className="font-bold text-lg text-moroccan-blue">
                                {slot.price} MAD
                              </div>
                              {slot.price !== basePrice && (
                                <div className="text-xs text-gray-500">
                                  +{slot.price - basePrice} MAD
                                </div>
                              )}
                            </div>

                            <Badge className={status.color}>
                              {status.label}
                            </Badge>

                            {slot.available ? (
                              <Check className="h-4 w-4 text-green-600" />
                            ) : (
                              <X className="h-4 w-4 text-red-600" />
                            )}
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>

                {selectedDayData.timeSlots.length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    No time slots available for this date
                  </div>
                )}
              </div>
            ) : (
              <div className="text-center py-8 text-gray-500">
                Please select a date to view available time slots
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Selection Summary */}
      {selectedDay && selectedTimeSlot && (
        <Card className="bg-gradient-to-r from-moroccan-blue to-blue-600 text-white">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <h4 className="text-lg font-semibold mb-2">Selected Booking</h4>
                <div className="space-y-1">
                  <div className="flex items-center space-x-2">
                    <CalendarIcon className="h-4 w-4" />
                    <span>{format(selectedDay, 'EEEE, MMMM d, yyyy')}</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Clock className="h-4 w-4" />
                    <span>{selectedTimeSlot.time} - {selectedTimeSlot.label}</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <DollarSign className="h-4 w-4" />
                    <span>{selectedTimeSlot.price} MAD per person</span>
                  </div>
                </div>
              </div>

              <div className="text-right">
                <div className="text-2xl font-bold">
                  {selectedTimeSlot.price} MAD
                </div>
                <div className="text-blue-100 text-sm">
                  per person
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/booking-form-modal.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Plus, Calendar, Users } from "lucide-react";

const bookingFormSchema = z.object({
  customerName: z.string().min(2, "Customer name is required"),
  customerPhone: z.string().min(10, "Valid phone number is required"),
  activityId: z.string().min(1, "Activity selection is required"),
  numberOfPeople: z.number().min(1, "At least 1 person required").max(20, "Maximum 20 people"),
  preferredDate: z.string().optional(),
  preferredTime: z.string().optional(),
  notes: z.string().optional(),
});

type BookingFormData = z.infer<typeof bookingFormSchema>;

interface BookingFormModalProps {
  trigger?: React.ReactNode;
  isOpen?: boolean;
  onClose?: () => void;
  activities?: any[];
}

export default function BookingFormModal({ 
  trigger, 
  isOpen = false, 
  onClose, 
  activities: passedActivities 
}: BookingFormModalProps) {
  const [open, setOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: fetchedActivities } = useQuery({
    queryKey: ["/api/activities"],
    enabled: !passedActivities,
  });

  const activities = passedActivities || fetchedActivities || [];
  const isControlled = isOpen !== undefined && onClose !== undefined;
  const modalOpen = isControlled ? isOpen : open;

  const form = useForm<BookingFormData>({
    resolver: zodResolver(bookingFormSchema),
    defaultValues: {
      customerName: "",
      customerPhone: "",
      activityId: "",
      numberOfPeople: 1,
      preferredDate: "",
      preferredTime: "",
      notes: "",
    },
  });

  const createBookingMutation = useMutation({
    mutationFn: async (data: BookingFormData) => {
      const response = await fetch("/api/bookings", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          ...data,
          preferredDate: data.preferredDate ? new Date(data.preferredDate) : undefined,
        }),
        credentials: "include",
      });

      if (!response.ok) {
        const errorData = await response.text();
        throw new Error(errorData || "Failed to create booking");
      }

      return await response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Booking created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/bookings"] });
      form.reset();
      setOpen(false);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create booking",
        variant: "destructive",
      });
    },
  });

  const onSubmit = async (data: BookingFormData) => {
    createBookingMutation.mutate(data);
  };

  // Calculate total amount based on activity and number of people
  const selectedActivityId = form.watch("activityId");
  const numberOfPeople = form.watch("numberOfPeople");
  const selectedActivity = Array.isArray(activities) ? activities.find((activity: any) => activity.id === selectedActivityId) : null;
  const totalAmount = selectedActivity ? selectedActivity.price * numberOfPeople : 0;

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {trigger || (
          <Button className="bg-moroccan-blue hover:bg-blue-600 text-white">
            <Plus className="h-4 w-4 mr-2" />
            New Booking
          </Button>
        )}
      </DialogTrigger>
      <DialogContent className="sm:max-w-[600px] max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-moroccan-blue">
            <Calendar className="h-5 w-5" />
            Create New Booking
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="customerName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Customer Name</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        placeholder="Enter customer name"
                        className="border-gray-300 focus:border-moroccan-blue"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="customerPhone"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Phone Number</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        placeholder="+212 6XX XXX XXX"
                        className="border-gray-300 focus:border-moroccan-blue"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="activityId"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Select Activity</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger className="border-gray-300 focus:border-moroccan-blue">
                        <SelectValue placeholder="Choose an activity" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {Array.isArray(activities) && activities.map((activity: any) => (
                        <SelectItem key={activity.id} value={activity.id}>
                          {activity.name} - {activity.price} MAD per person
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <FormField
                control={form.control}
                name="numberOfPeople"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center gap-2">
                      <Users className="h-4 w-4" />
                      Number of People
                    </FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="number"
                        min="1"
                        max="20"
                        onChange={(e) => field.onChange(Number(e.target.value))}
                        className="border-gray-300 focus:border-moroccan-blue"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="preferredDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Preferred Date</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="date"
                        min={new Date().toISOString().split('T')[0]}
                        className="border-gray-300 focus:border-moroccan-blue"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="preferredTime"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Preferred Time</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger className="border-gray-300 focus:border-moroccan-blue">
                          <SelectValue placeholder="Select time" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="morning">Morning (6:00 - 10:00)</SelectItem>
                        <SelectItem value="afternoon">Afternoon (14:00 - 18:00)</SelectItem>
                        <SelectItem value="evening">Evening (18:00 - 22:00)</SelectItem>
                        <SelectItem value="sunrise">Sunrise (5:30 - 7:00)</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="notes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Additional Notes</FormLabel>
                  <FormControl>
                    <Textarea
                      {...field}
                      placeholder="Special requests, dietary requirements, etc."
                      className="border-gray-300 focus:border-moroccan-blue min-h-[80px]"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {selectedActivity && (
              <div className="space-y-4">
                {/* Price Comparison Section */}
                <div className="bg-blue-50 p-4 rounded-lg border border-blue-200">
                  <h4 className="font-semibold text-moroccan-blue mb-3 flex items-center gap-2">
                    <span>ðŸ’°</span>
                    Price Comparison Analysis
                  </h4>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                    <div className="bg-white p-3 rounded border">
                      <div className="font-medium text-green-700 mb-1">Our Price</div>
                      <div className="text-xl font-bold text-green-600">{selectedActivity.price} MAD</div>
                      <div className="text-xs text-gray-600">Best Value</div>
                    </div>
                    <div className="bg-white p-3 rounded border">
                      <div className="font-medium text-orange-700 mb-1">GetYourGuide</div>
                      <div className="text-xl font-bold text-orange-600">{selectedActivity.getyourguidePrice || selectedActivity.price + 150} MAD</div>
                      <div className="text-xs text-red-600">+{Math.round(((selectedActivity.getyourguidePrice || selectedActivity.price + 150) - selectedActivity.price) / selectedActivity.price * 100)}% more</div>
                    </div>
                    <div className="bg-white p-3 rounded border">
                      <div className="font-medium text-blue-700 mb-1">You Save</div>
                      <div className="text-xl font-bold text-blue-600">{((selectedActivity.getyourguidePrice || selectedActivity.price + 150) - selectedActivity.price)} MAD</div>
                      <div className="text-xs text-green-600">Per person</div>
                    </div>
                  </div>
                </div>

                {/* Seasonal Pricing */}
                <div className="bg-orange-50 p-4 rounded-lg border border-orange-200">
                  <h4 className="font-semibold text-moroccan-blue mb-3 flex items-center gap-2">
                    <span>ðŸ“…</span>
                    Seasonal Pricing
                  </h4>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-3 text-sm">
                    <div className="bg-white p-3 rounded border">
                      <div className="font-medium text-blue-700">Low Season</div>
                      <div className="text-lg font-bold text-blue-600">{Math.round(selectedActivity.price * 0.85)} MAD</div>
                      <div className="text-xs text-gray-600">Nov-Feb</div>
                    </div>
                    <div className="bg-white p-3 rounded border border-green-300">
                      <div className="font-medium text-green-700">Regular Season âœ“</div>
                      <div className="text-lg font-bold text-green-600">{selectedActivity.price} MAD</div>
                      <div className="text-xs text-gray-600">Current Price</div>
                    </div>
                    <div className="bg-white p-3 rounded border">
                      <div className="font-medium text-red-700">High Season</div>
                      <div className="text-lg font-bold text-red-600">{Math.round(selectedActivity.price * 1.25)} MAD</div>
                      <div className="text-xs text-gray-600">Jun-Aug</div>
                    </div>
                  </div>
                </div>

                {/* Booking Summary */}
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h4 className="font-semibold text-moroccan-blue mb-2">Booking Summary</h4>
                  <div className="space-y-1 text-sm">
                    <div className="flex justify-between">
                      <span>Activity:</span>
                      <span className="font-medium">{selectedActivity.name}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Price per person:</span>
                      <span>{selectedActivity.price} MAD</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Number of people:</span>
                      <span>{numberOfPeople}</span>
                    </div>
                    <div className="flex justify-between border-t pt-2 font-semibold text-moroccan-blue">
                      <span>Total Amount:</span>
                      <span>{totalAmount.toLocaleString()} MAD</span>
                    </div>
                    <div className="flex justify-between text-xs text-green-600">
                      <span>Total Savings vs GetYourGuide:</span>
                      <span>{(((selectedActivity.getyourguidePrice || selectedActivity.price + 150) - selectedActivity.price) * numberOfPeople).toLocaleString()} MAD</span>
                    </div>
                  </div>
                </div>
              </div>
            )}

            <div className="flex justify-end gap-3">
              <Button
                type="button"
                variant="outline"
                onClick={() => setOpen(false)}
                disabled={createBookingMutation.isPending}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                className="bg-moroccan-red hover:bg-red-600 text-white"
                disabled={createBookingMutation.isPending}
              >
                {createBookingMutation.isPending ? "Creating..." : "Create Booking"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/competitor-price-m.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { TrendingUp, TrendingDown, AlertTriangle, RefreshCw, Target, DollarSign } from "lucide-react";

interface CompetitorPrice {
  id: string;
  activityName: string;
  ourPrice: number;
  getYourGuidePrice: number;
  viatorPrice: number;
  airbnbPrice: number;
  lastUpdated: Date;
  priceChange: number;
  competitiveAdvantage: number;
  recommendedAction: string;
}

interface PriceAlert {
  id: string;
  activity: string;
  type: 'price_drop' | 'price_increase' | 'competitive_threat';
  message: string;
  severity: 'low' | 'medium' | 'high';
  timestamp: Date;
}

export default function CompetitorPriceMonitor() {
  const [competitorPrices, setCompetitorPrices] = useState<CompetitorPrice[]>([]);
  const [priceAlerts, setPriceAlerts] = useState<PriceAlert[]>([]);
  const [isUpdating, setIsUpdating] = useState(false);
  const [editingPrice, setEditingPrice] = useState<string | null>(null);

  useEffect(() => {
    // Simulate real competitor price data
    const mockData: CompetitorPrice[] = [
      {
        id: "1",
        activityName: "Hot Air Balloon Ride",
        ourPrice: 2000,
        getYourGuidePrice: 2350,
        viatorPrice: 2280,
        airbnbPrice: 2150,
        lastUpdated: new Date(),
        priceChange: -50,
        competitiveAdvantage: 350,
        recommendedAction: "Maintain current pricing - strong advantage"
      },
      {
        id: "2", 
        activityName: "3-Day Desert Experience",
        ourPrice: 5000,
        getYourGuidePrice: 5800,
        viatorPrice: 5650,
        airbnbPrice: 5200,
        lastUpdated: new Date(),
        priceChange: 100,
        competitiveAdvantage: 800,
        recommendedAction: "Consider 10% price increase"
      },
      {
        id: "3",
        activityName: "Essaouira Day Trip", 
        ourPrice: 1500,
        getYourGuidePrice: 1680,
        viatorPrice: 1620,
        airbnbPrice: 1550,
        lastUpdated: new Date(),
        priceChange: 0,
        competitiveAdvantage: 180,
        recommendedAction: "Optimal pricing position"
      },
      {
        id: "4",
        activityName: "Ourika Valley Adventure",
        ourPrice: 1500,
        getYourGuidePrice: 1750,
        viatorPrice: 1480,
        airbnbPrice: 1520,
        lastUpdated: new Date(),
        priceChange: -20,
        competitiveAdvantage: 250,
        recommendedAction: "Monitor Viator closely"
      },
      {
        id: "5",
        activityName: "Ouzoud Waterfalls Tour",
        ourPrice: 1500,
        getYourGuidePrice: 1650,
        viatorPrice: 1580,
        airbnbPrice: 1490,
        lastUpdated: new Date(),
        priceChange: 30,
        competitiveAdvantage: 150,
        recommendedAction: "Slight price adjustment recommended"
      }
    ];

    const mockAlerts: PriceAlert[] = [
      {
        id: "1",
        activity: "Hot Air Balloon Ride",
        type: "price_drop",
        message: "GetYourGuide reduced price by 50 MAD - still 350 MAD above us",
        severity: "low",
        timestamp: new Date(Date.now() - 2 * 60 * 60 * 1000)
      },
      {
        id: "2", 
        activity: "3-Day Desert Experience",
        type: "competitive_threat",
        message: "Viator offering 10% discount promotion - monitor closely",
        severity: "medium",
        timestamp: new Date(Date.now() - 4 * 60 * 60 * 1000)
      },
      {
        id: "3",
        activity: "Ourika Valley Adventure", 
        type: "price_drop",
        message: "Viator price dropped to 1480 MAD - 20 MAD below our price",
        severity: "high",
        timestamp: new Date(Date.now() - 6 * 60 * 60 * 1000)
      }
    ];

    setCompetitorPrices(mockData);
    setPriceAlerts(mockAlerts);
  }, []);

  const updatePrices = async () => {
    setIsUpdating(true);
    // Simulate API call to update competitor prices
    setTimeout(() => {
      setCompetitorPrices(prev => prev.map(price => ({
        ...price,
        lastUpdated: new Date(),
        priceChange: Math.floor(Math.random() * 100) - 50
      })));
      setIsUpdating(false);
    }, 2000);
  };

  const updateOurPrice = (id: string, newPrice: number) => {
    setCompetitorPrices(prev => prev.map(price => 
      price.id === id 
        ? { 
            ...price, 
            ourPrice: newPrice,
            competitiveAdvantage: Math.min(price.getYourGuidePrice, price.viatorPrice, price.airbnbPrice) - newPrice
          }
        : price
    ));
    setEditingPrice(null);
  };

  const getLowestCompetitor = (price: CompetitorPrice) => {
    const competitors = [
      { name: "GetYourGuide", price: price.getYourGuidePrice },
      { name: "Viator", price: price.viatorPrice },
      { name: "Airbnb", price: price.airbnbPrice }
    ];
    return competitors.reduce((lowest, current) => 
      current.price < lowest.price ? current : lowest
    );
  };

  const totalCompetitiveAdvantage = competitorPrices.reduce((sum, price) => sum + price.competitiveAdvantage, 0);
  const avgAdvantage = Math.round(totalCompetitiveAdvantage / competitorPrices.length);

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold text-gray-900">Competitor Price Monitoring</h2>
        <Button onClick={updatePrices} disabled={isUpdating}>
          <RefreshCw className={`w-4 h-4 mr-2 ${isUpdating ? 'animate-spin' : ''}`} />
          {isUpdating ? 'Updating...' : 'Update Prices'}
        </Button>
      </div>

      {/* Price Alerts */}
      {priceAlerts.length > 0 && (
        <div className="space-y-3">
          <h3 className="text-lg font-semibold">Price Alerts</h3>
          {priceAlerts.map((alert) => (
            <Alert key={alert.id} className={`
              ${alert.severity === 'high' ? 'border-red-200 bg-red-50' : 
                alert.severity === 'medium' ? 'border-yellow-200 bg-yellow-50' : 
                'border-blue-200 bg-blue-50'}
            `}>
              <AlertTriangle className={`h-4 w-4 ${
                alert.severity === 'high' ? 'text-red-600' :
                alert.severity === 'medium' ? 'text-yellow-600' : 
                'text-blue-600'
              }`} />
              <AlertDescription>
                <div className="flex justify-between items-start">
                  <div>
                    <span className="font-medium">{alert.activity}</span>
                    <p className="text-sm">{alert.message}</p>
                  </div>
                  <Badge variant={
                    alert.severity === 'high' ? 'destructive' :
                    alert.severity === 'medium' ? 'default' : 'secondary'
                  }>
                    {alert.severity}
                  </Badge>
                </div>
              </AlertDescription>
            </Alert>
          ))}
        </div>
      )}

      {/* Summary Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Avg Competitive Advantage</CardTitle>
            <Target className="h-4 w-4 text-green-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-600">{avgAdvantage} MAD</div>
            <p className="text-xs text-gray-600">Per booking saved vs competitors</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Price Leader Activities</CardTitle>
            <TrendingUp className="h-4 w-4 text-blue-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-blue-600">
              {competitorPrices.filter(p => p.competitiveAdvantage > 0).length}/5
            </div>
            <p className="text-xs text-gray-600">Activities below competitor prices</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Revenue Opportunity</CardTitle>
            <DollarSign className="h-4 w-4 text-purple-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-purple-600">
              {totalCompetitiveAdvantage.toLocaleString()} MAD
            </div>
            <p className="text-xs text-gray-600">Monthly potential from price optimization</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Market Position</CardTitle>
            <Target className="h-4 w-4 text-orange-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-orange-600">Strong</div>
            <p className="text-xs text-gray-600">Overall competitive position</p>
          </CardContent>
        </Card>
      </div>

      {/* Detailed Price Comparison */}
      <Card>
        <CardHeader>
          <CardTitle>Detailed Price Analysis</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {competitorPrices.map((price) => {
              const lowestCompetitor = getLowestCompetitor(price);
              return (
                <div key={price.id} className="p-4 border rounded-lg">
                  <div className="flex items-center justify-between mb-3">
                    <h4 className="text-lg font-semibold">{price.activityName}</h4>
                    <div className="flex items-center gap-2">
                      {price.priceChange !== 0 && (
                        <Badge variant={price.priceChange > 0 ? "destructive" : "default"}>
                          {price.priceChange > 0 ? <TrendingUp className="w-3 h-3 mr-1" /> : <TrendingDown className="w-3 h-3 mr-1" />}
                          {Math.abs(price.priceChange)} MAD
                        </Badge>
                      )}
                      <Badge variant={price.competitiveAdvantage > 0 ? "default" : "destructive"}>
                        {price.competitiveAdvantage > 0 ? '+' : ''}{price.competitiveAdvantage} MAD advantage
                      </Badge>
                    </div>
                  </div>

                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-3">
                    <div className="text-center p-3 bg-blue-50 rounded-lg">
                      <div className="text-sm text-gray-600">Our Price</div>
                      {editingPrice === price.id ? (
                        <Input
                          type="number"
                          defaultValue={price.ourPrice}
                          className="text-center mt-1"
                          onBlur={(e) => updateOurPrice(price.id, parseInt(e.target.value))}
                          onKeyDown={(e) => {
                            if (e.key === 'Enter') {
                              updateOurPrice(price.id, parseInt((e.target as HTMLInputElement).value));
                            }
                          }}
                          autoFocus
                        />
                      ) : (
                        <div 
                          className="text-xl font-bold text-blue-600 cursor-pointer hover:bg-blue-100 rounded px-2 py-1"
                          onClick={() => setEditingPrice(price.id)}
                        >
                          {price.ourPrice} MAD
                        </div>
                      )}
                    </div>
                    <div className="text-center p-3 bg-gray-50 rounded-lg">
                      <div className="text-sm text-gray-600">GetYourGuide</div>
                      <div className="text-xl font-bold">{price.getYourGuidePrice} MAD</div>
                    </div>
                    <div className="text-center p-3 bg-gray-50 rounded-lg">
                      <div className="text-sm text-gray-600">Viator</div>
                      <div className="text-xl font-bold">{price.viatorPrice} MAD</div>
                    </div>
                    <div className="text-center p-3 bg-gray-50 rounded-lg">
                      <div className="text-sm text-gray-600">Airbnb</div>
                      <div className="text-xl font-bold">{price.airbnbPrice} MAD</div>
                    </div>
                  </div>

                  <div className="flex items-center justify-between text-sm">
                    <div>
                      <span className="text-gray-600">Lowest competitor: </span>
                      <span className="font-medium">{lowestCompetitor.name} ({lowestCompetitor.price} MAD)</span>
                    </div>
                    <div>
                      <span className="text-gray-600">Last updated: </span>
                      <span className="font-medium">{price.lastUpdated.toLocaleTimeString()}</span>
                    </div>
                  </div>

                  <div className="mt-2 p-2 bg-yellow-50 rounded text-sm">
                    <span className="font-medium text-yellow-800">Recommendation: </span>
                    <span className="text-yellow-700">{price.recommendedAction}</span>
                  </div>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>

      {/* Pricing Strategies */}
      <Card>
        <CardHeader>
          <CardTitle>Dynamic Pricing Strategies</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-3">
              <h4 className="font-semibold text-green-700">Competitive Advantages</h4>
              <ul className="space-y-2 text-sm text-gray-700">
                <li>â€¢ 15-20% lower prices than GetYourGuide across all activities</li>
                <li>â€¢ Hot Air Balloon: 350 MAD customer savings vs competitors</li>
                <li>â€¢ Desert Experience: 800 MAD advantage - room for 10% increase</li>
                <li>â€¢ Strong position in day trip market with optimal pricing</li>
                <li>â€¢ Average 200+ MAD savings per booking for customers</li>
              </ul>
            </div>
            <div className="space-y-3">
              <h4 className="font-semibold text-red-700">Price Optimization Opportunities</h4>
              <ul className="space-y-2 text-sm text-gray-700">
                <li>â€¢ Monitor Viator's Ourika Valley pricing (20 MAD below us)</li>
                <li>â€¢ Consider 5-10% increase on Desert Experience</li>
                <li>â€¢ Set automatic alerts for 15%+ competitor price changes</li>
                <li>â€¢ Implement seasonal pricing adjustments (high/low season)</li>
                <li>â€¢ Track promotional campaigns from competitors</li>
              </ul>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

