        languages: ['Arabic', 'French', 'Italian'],
        specialties: ['Coastal Tours', 'Art & Crafts'],
        status: 'off_duty',
        rating: 4.6,
        nextAvailable: new Date(Date.now() + 16 * 60 * 60 * 1000)
      }
