import { useState } from "react";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Sheet, SheetContent, SheetHeader, SheetTitle, SheetTrigger } from "@/components/ui/sheet";
import { Mountain, Menu, Calendar, MapPin, Phone, Globe } from "lucide-react";
import { useLanguage } from "@/hooks/useLanguage";
import { useAuth } from "@/hooks/use-auth";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

export default function Navbar() {
  const [location] = useLocation();
  const [isOpen, setIsOpen] = useState(false);
  const { language, changeLanguage, t } = useLanguage();
  const { user } = useAuth();

  const navItems = [
    { href: "/", label: t('nav.home') },
    { href: "/activities", label: t('nav.activities') },
    { href: "/booking", label: "Booking" },
    { href: "/reviews", label: t('nav.reviews') },
  ];

  // Only show admin link if user is authenticated and has admin role
  if (user && (user.role === 'admin' || user.role === 'superadmin')) {
    navItems.push({ href: "/admin", label: t('nav.admin') });
  }

  return (
    <nav className="bg-white shadow-lg sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <Link href="/">
            <div className="flex items-center cursor-pointer">
              <Mountain className="text-moroccan-red text-2xl mr-2" />
              <span className="font-playfair text-xl font-bold text-moroccan-blue">
                MarrakechDunes
              </span>
            </div>
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex space-x-6 items-center">
            {navItems.map((item) => (
              <Link key={item.href} href={item.href}>
                <span
                  className={`text-gray-700 hover:text-moroccan-red transition-colors cursor-pointer ${
                    location === item.href ? "text-moroccan-red font-medium" : ""
                  }`}
                >
                  {item.label}
                </span>
              </Link>
            ))}

            {/* Language Switcher */}
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="outline" size="sm" className="border-moroccan-blue text-moroccan-blue hover:bg-moroccan-blue hover:text-white">
                  <Globe className="w-4 h-4 mr-2" />
                  {language.toUpperCase()}
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent>
                <DropdownMenuItem onClick={() => changeLanguage("en")}>
                  English
                </DropdownMenuItem>
                <DropdownMenuItem onClick={() => changeLanguage("fr")}>
                  FranÃ§ais
                </DropdownMenuItem>
                <DropdownMenuItem onClick={() => changeLanguage("ar")}>
                  Ø§Ù„Ø¹Ø±Ø¨ÙŠØ©
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>

          {/* Mobile Navigation Trigger */}
          <div className="md:hidden">
            <Sheet open={isOpen} onOpenChange={setIsOpen}>
              <SheetTrigger asChild>
                <Button variant="ghost" size="sm">
                  <Menu className="h-6 w-6" />
                </Button>
              </SheetTrigger>
              <SheetContent side="right" className="w-80">
                <SheetHeader>
                  <SheetTitle className="flex items-center">
                    <Mountain className="text-moroccan-red mr-2" />
                    <span className="font-playfair text-moroccan-blue">MarrakechDunes</span>
                  </SheetTitle>
                </SheetHeader>
                <div className="flex flex-col space-y-4 mt-8">
                  {navItems.map((item) => (
                    <Link key={item.href} href={item.href}>
                      <div
                        className={`text-lg hover:text-moroccan-red transition-colors cursor-pointer p-2 rounded ${
                          location === item.href ? "text-moroccan-red bg-moroccan-sand" : ""
                        }`}
                        onClick={() => setIsOpen(false)}
                      >
                        {item.label}
                      </div>
                    </Link>
                  ))}
                  <div className="border-t pt-4 mt-4">
                    <Link href="/admin/login">
                      <div
                        className="text-lg hover:text-moroccan-red transition-colors cursor-pointer p-2 rounded"
                        onClick={() => setIsOpen(false)}
                      >
                        Admin Login
                      </div>
                    </Link>
                  </div>

                  {/* Contact Info */}
                  <div className="border-t pt-4 mt-4 space-y-3">
                    <div className="flex items-center">
                      <MapPin className="w-4 h-4 text-moroccan-red mr-3" />
                      <span className="text-sm">54 Riad Zitoun Lakdim, Marrakech</span>
                    </div>
                    <div className="flex items-center">
                      <Phone className="w-4 h-4 text-moroccan-red mr-3" />
                      <span className="text-sm">WhatsApp Available</span>
                    </div>
                  </div>
                </div>
              </SheetContent>
            </Sheet>
          </div>
        </div>
      </div>
    </nav>
  );
}

client/src/components/payment-management.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  Banknote, 
  CheckCircle, 
  Clock, 
  AlertCircle, 
  Edit, 
  Receipt,
  Calculator,
  CreditCard
} from "lucide-react";
import type { BookingWithActivity } from "@shared/schema";

interface PaymentManagementProps {
  booking: BookingWithActivity;
}

export default function PaymentManagement({ booking }: PaymentManagementProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isOpen, setIsOpen] = useState(false);
  const [paymentAmount, setPaymentAmount] = useState<number>(0);
  const [paymentType, setPaymentType] = useState<'full' | 'deposit' | 'balance'>('full');

  const updatePaymentMutation = useMutation({
    mutationFn: async (data: {
      bookingId: string;
      paymentStatus: string;
      paidAmount: number;
      paymentMethod: string;
      depositAmount?: number;
    }) => {
      const response = await apiRequest("PATCH", `/api/admin/bookings/${data.bookingId}/payment`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/bookings"] });
      toast({
        title: "Payment Updated",
        description: "Payment status has been successfully updated.",
      });
      setIsOpen(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Update Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handlePaymentUpdate = () => {
    let newPaymentStatus: string;
    let newPaidAmount: number;
    let depositAmount: number | undefined;

    const currentPaid = booking.paidAmount || 0;

    switch (paymentType) {
      case 'full':
        newPaymentStatus = 'fully_paid';
        newPaidAmount = booking.totalAmount;
        break;
      case 'deposit':
        newPaymentStatus = 'deposit_paid';
        newPaidAmount = paymentAmount;
        depositAmount = paymentAmount;
        break;
      case 'balance':
        newPaymentStatus = 'fully_paid';
        newPaidAmount = currentPaid + paymentAmount;
        break;
      default:
        return;
    }

    updatePaymentMutation.mutate({
      bookingId: booking.id,
      paymentStatus: newPaymentStatus,
      paidAmount: newPaidAmount,
      paymentMethod: paymentType === 'deposit' ? 'cash_deposit' : 'cash',
      depositAmount,
    });
  };

  const getPaymentStatusColor = (status: string) => {
    switch (status) {
      case 'fully_paid':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'deposit_paid':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'unpaid':
        return 'bg-red-100 text-red-800 border-red-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getPaymentStatusIcon = (status: string) => {
    switch (status) {
      case 'fully_paid':
        return <CheckCircle className="w-4 h-4" />;
      case 'deposit_paid':
        return <Clock className="w-4 h-4" />;
      case 'unpaid':
        return <AlertCircle className="w-4 h-4" />;
      default:
        return <Clock className="w-4 h-4" />;
    }
  };

  const currentPaid = booking.paidAmount || 0;
  const remainingAmount = booking.totalAmount - currentPaid;
  const isFullyPaid = booking.paymentStatus === 'fully_paid';
  const isDepositPaid = booking.paymentStatus === 'deposit_paid';

  return (
    <Card className="w-full">
      <CardHeader className="pb-3">
        <CardTitle className="flex items-center gap-2 text-lg">
          <Receipt className="w-5 h-5 text-moroccan-blue" />
          Payment Management
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Payment Status */}
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <span className="text-sm font-medium">Status:</span>
            <Badge className={`${getPaymentStatusColor(booking.paymentStatus)} flex items-center gap-1`}>
              {getPaymentStatusIcon(booking.paymentStatus)}
              {booking.paymentStatus?.replace('_', ' ').toUpperCase()}
            </Badge>
          </div>
          <Dialog open={isOpen} onOpenChange={setIsOpen}>
            <DialogTrigger asChild>
              <Button variant="outline" size="sm" className="flex items-center gap-1">
                <Edit className="w-4 h-4" />
                Update Payment
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-md">
              <DialogHeader>
                <DialogTitle className="flex items-center gap-2">
                  <Banknote className="w-5 h-5" />
                  Update Payment Status
                </DialogTitle>
              </DialogHeader>

              <div className="space-y-4">
                <div className="bg-moroccan-sand/20 p-3 rounded-lg">
                  <div className="text-sm space-y-1">
                    <div className="flex justify-between">
                      <span>Total Amount:</span>
                      <span className="font-medium">{booking.totalAmount} MAD</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Paid Amount:</span>
                      <span className="font-medium text-green-600">{currentPaid} MAD</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Remaining:</span>
                      <span className="font-medium text-orange-600">{remainingAmount} MAD</span>
                    </div>
                  </div>
                </div>

                <div className="space-y-3">
                  <div>
                    <Label htmlFor="paymentType">Payment Type</Label>
                    <Select value={paymentType} onValueChange={(value: 'full' | 'deposit' | 'balance') => setPaymentType(value)}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {!isFullyPaid && (
                          <SelectItem value="full">
                            <div className="flex items-center gap-2">
                              <CreditCard className="w-4 h-4" />
                              Full Payment ({booking.totalAmount} MAD)
                            </div>
                          </SelectItem>
                        )}
                        {!isDepositPaid && !isFullyPaid && (
                          <SelectItem value="deposit">
                            <div className="flex items-center gap-2">
                              <Calculator className="w-4 h-4" />
                              Deposit Payment
                            </div>
                          </SelectItem>
                        )}
                        {isDepositPaid && !isFullyPaid && (
                          <SelectItem value="balance">
                            <div className="flex items-center gap-2">
                              <Banknote className="w-4 h-4" />
                              Balance Payment ({remainingAmount} MAD)
                            </div>
                          </SelectItem>
                        )}
                      </SelectContent>
                    </Select>
                  </div>

                  {(paymentType === 'deposit' || paymentType === 'balance') && (
                    <div>
                      <Label htmlFor="amount">Amount (MAD)</Label>
                      <Input
                        id="amount"
                        type="number"
                        min="1"
                        max={paymentType === 'deposit' ? booking.totalAmount : remainingAmount}
                        value={paymentAmount}
                        onChange={(e) => setPaymentAmount(parseInt(e.target.value) || 0)}
                        placeholder={`Enter amount`}
                      />
                      {paymentType === 'deposit' && (
                        <p className="text-xs text-gray-500 mt-1">
                          Recommended: {Math.round(booking.totalAmount * 0.3)} MAD (30%)
                        </p>
                      )}
                    </div>
                  )}
                </div>

                <div className="flex gap-2 pt-4">
                  <Button
                    variant="outline"
                    onClick={() => setIsOpen(false)}
                    className="flex-1"
                  >
                    Cancel
                  </Button>
                  <Button
                    onClick={handlePaymentUpdate}
                    disabled={updatePaymentMutation.isPending || (paymentType !== 'full' && paymentAmount <= 0)}
                    className="flex-1 bg-moroccan-red hover:bg-red-600"
                  >
                    {updatePaymentMutation.isPending ? "Updating..." : "Update Payment"}
                  </Button>
                </div>
              </div>
            </DialogContent>
          </Dialog>
        </div>

        <Separator />

        {/* Payment Details */}
        <div className="grid grid-cols-2 gap-4 text-sm">
          <div className="space-y-2">
            <div className="flex justify-between">
              <span className="text-gray-600">Total Amount:</span>
              <span className="font-medium">{booking.totalAmount} MAD</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-600">Payment Method:</span>
              <span className="font-medium capitalize">
                {booking.paymentMethod?.replace('_', ' ') || 'Cash'}
              </span>
            </div>
